sysauto_script.script:
// Get today's time and date
	var rightNow = new GlideDateTime();
	gs.addInfoMessage('running script1');
gs.warn('check vish');
	// Query the database for NeedIt Task records with Due date field values older
	// then the current time.  Only return NeedIt Task records that do not have
	// a State field value of Closed Complete.
	var activeOpenings = new GlideRecord('x_290998_indicomm_job_openings');
	activeOpenings.addQuery('u_required_date','>',rightNow);
	 activeOpenings.addQuery('active','=','true');
	gs.addInfoMessage('running script');
gs.warn('check vish1');
	activeOpenings.query();
	// Write a log message for each overdue NeedIt Task Record
	while(activeOpenings.next()){
		gs.warn('check vish2' + activeOpenings.number);
			gs.info("Active Job openings " + activeOpenings.number);		gs.eventQueue('x_290998_indicomm.overdueJobOpenings',activeOpenings,gs.getUserDisplayName(),gs.getUserName());


	}
